mapper.select:
select account_name, account_password, flag, deposit, customer_card_number
from tb_customer_account
where account_name = #{accountName} and account_password = #{accountPassword}

mapper.update:
update tb_customer_account

    account_password = #{accountPassword},
    flag = #{flag},
    customer_card_number = #{customerCardNumber}

where account_name = #{accountName}
