mapper.select:
select room_id, room_floor, room_desc, room_state, room_type, room_price
from tb_room


        and room_type = #{roomCondition.roomType}


        and room_state = #{roomCondition.roomState}


limit #{rowIndex}, #{pageSize}

select count(1)
from tb_room


        and room_type = #{roomCondition.roomType}


        and room_state = #{roomCondition.roomState}

mapper.update:
update tb_room

    room_floor = #{roomFloor}
    room_desc = #{roomDesc}
    room_state = #{roomState}
    room_type = #{roomType}
    room_price = #{roomPrice}

where room_id = #{roomId}
